//initialState
//action
const initialState = { dataArr: [], selectedTag: null };

const reducerFunc = (state = initialState, action) => {
    //action -----------------------
    // {
    //     type: "YSAJSACS_AJNSCN_AHS",
    //     payload: value for updation
    // }
    const { type, payload } = action;

    switch (type) {
        case SET_SELECTED_TAG: {
            return {
                ...state,
                selectedTag: payload,
            }
        }

        case UNSET_STATE:{
            return {
                selectedTag: null,
                dataArr: []
            }
        }
        case SET_DATA_ARRAY: {
            return {
                ...state,
                dataArr: payload,
            }
        }
        default:
            return state   
    }
}
