//actions and action creators
export const setSelectedTag = (selectedTag) => {
    return{
        type: "SET_SELECTED_TAG",
        payload: selectedTag,
    }
}
export const unsetState = () => {
    return{
        type: "UNSET_STATE",
    }
}
export const setDataArray = (dataArray) => {
    return{
        type: "SET_DATA_ARRAY",
        payload: dataArray,
    }
}
